import React, { useCallback, useState } from 'react';
import { ActionList, AppProvider, Avatar, Card, Frame, TopBar } from '@shopify/polaris';
import { ArrowLeftMinor } from '@shopify/polaris-icons';

export default function TopBarExample() {
    const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);
    const [isSearchActive, setIsSearchActive] = useState(false);
    const [searchValue, setSearchValue] = useState('');

    const toggleIsUserMenuOpen = useCallback(() => setIsUserMenuOpen(isUserMenuOpen => !isUserMenuOpen), []);

    const handleSearchResultsDismiss = useCallback(() => {
        setIsSearchActive(false);
        setSearchValue('');
    }, []);

    const handleSearchChange = useCallback(value => {
        setSearchValue(value);
        setIsSearchActive(value.length > 0);
    }, []);

    const handleNavigationToggle = useCallback(() => {
        console.log('toggle navigation visibility');
    }, []);

    const theme = {
        colors: {
            topBar: {
                background: '#357997'
            }
        },
        logo: {
            width: 124,
            topBarSource: 'CV',
            url: 'http://smartcv.ca',
            accessibilityLabel: 'CV Generator'
        }
    };

    const userMenuMarkup = (
        <TopBar.UserMenu
            actions={[
                {
                    items: [{ content: 'Back to Shopify', icon: ArrowLeftMinor }]
                },
                {
                    items: [{ content: 'Community forums' }]
                }
            ]}
            name="Dharma"
            detail="Jaded Pixel"
            initials="D"
            open={isUserMenuOpen}
            onToggle={toggleIsUserMenuOpen}
        />
    );

    const searchResultsMarkup = (
        <Card>
            <ActionList items={[{ content: 'Shopify help center' }, { content: 'Community forums' }]} />
        </Card>
    );

    const searchFieldMarkup = <TopBar.SearchField onChange={handleSearchChange} value={searchValue} placeholder="Search" showFocusBorder />;

    const topBarMarkup = (
        <TopBar
            showNavigationToggle
            userMenu={userMenuMarkup}
            searchResultsVisible={isSearchActive}
            searchField={searchFieldMarkup}
            searchResults={searchResultsMarkup}
            onSearchResultsDismiss={handleSearchResultsDismiss}
            onNavigationToggle={handleNavigationToggle}
        />
    );

    return (
        <div style={{ height: '250px' }}>
            <AppProvider
                theme={theme}
                i18n={{
                    Polaris: {
                        Avatar: {
                            label: 'Avatar',
                            labelWithInitials: 'Avatar with initials {initials}'
                        },
                        Frame: { skipToContent: 'Skip to content' },
                        TopBar: {
                            toggleMenuLabel: 'Toggle menu',
                            SearchField: {
                                clearButtonLabel: 'Clear',
                                search: 'Search'
                            }
                        }
                    }
                }}
            >
                <Frame topBar={topBarMarkup} />
            </AppProvider>
        </div>
    );
}
